import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [f_Id, setF_Id] = useState('');
  const [f_Name, setF_Name] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Course, setF_Course] = useState([]);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/employees');
      setEmployees(response.data);
    } catch (err) {
      setError('Error fetching employees');
    }
  };

  // Handle Edit
  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setF_Id(employee.f_Id);
    setF_Name(employee.f_Name);
    setF_Email(employee.f_Email);
    setF_Mobile(employee.f_Mobile);
    setF_Designation(employee.f_Designation);
    setF_gender(employee.f_gender);
    setF_Course(employee.f_Course);
  };

  // Handle Update
  const handleUpdate = async (id) => {
    try {
      const updatedEmployee = {
        f_Id,
        f_Name,
        f_Email,
        f_Mobile,
        f_Designation,
        f_gender,
        f_Course
      };

      await axios.put(`http://localhost:5000/update-employee/${id}`, updatedEmployee);
      setSuccess('Employee updated successfully');
      setEditingEmployee(null); // Go back to the list after update
      fetchEmployees();
    } catch (err) {
      setError('Error updating employee');
    }
  };

  // Handle Cancel
  const handleCancel = () => {
    setEditingEmployee(null); // Go back to the list if editing is cancelled
    setSuccess('');
    setError('');
  };

  return (
    <div>
      <h2>Employee Management</h2>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      {editingEmployee ? (
        <div>
          <h3>Edit Employee</h3>
          <form onSubmit={(e) => e.preventDefault()}>
            <input
              type="text"
              placeholder="Employee ID"
              value={f_Id}
              onChange={(e) => setF_Id(e.target.value)}
            />
            <input
              type="text"
              placeholder="Name"
              value={f_Name}
              onChange={(e) => setF_Name(e.target.value)}
            />
            <input
              type="email"
              placeholder="Email"
              value={f_Email}
              onChange={(e) => setF_Email(e.target.value)}
            />
            <input
              type="text"
              placeholder="Mobile"
              value={f_Mobile}
              onChange={(e) => setF_Mobile(e.target.value)}
            />
            <select
              value={f_Designation}
              onChange={(e) => setF_Designation(e.target.value)}
            >
              <option value="">Select Designation</option>
              <option value="HR">HR</option>
              <option value="Manager">Manager</option>
              <option value="Sales">Sales</option>
            </select>
            <div>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="Male"
                  checked={f_gender === 'Male'}
                  onChange={(e) => setF_gender(e.target.value)}
                />
                Male
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="Female"
                  checked={f_gender === 'Female'}
                  onChange={(e) => setF_gender(e.target.value)}
                />
                Female
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="Other"
                  checked={f_gender === 'Other'}
                  onChange={(e) => setF_gender(e.target.value)}
                />
                Other
              </label>
            </div>
            <div>
              <label>
                <input
                  type="checkbox"
                  value="BSC"
                  checked={f_Course.includes('BSC')}
                  onChange={(e) => {
                    const { checked } = e.target;
                    setF_Course(checked ? [...f_Course, 'BSC'] : f_Course.filter((c) => c !== 'BSC'));
                  }}
                />
                BSC
              </label>
              <label>
                <input
                  type="checkbox"
                  value="BCA"
                  checked={f_Course.includes('BCA')}
                  onChange={(e) => {
                    const { checked } = e.target;
                    setF_Course(checked ? [...f_Course, 'BCA'] : f_Course.filter((c) => c !== 'BCA'));
                  }}
                />
                BCA
              </label>
              <label>
                <input
                  type="checkbox"
                  value="MCA"
                  checked={f_Course.includes('MCA')}
                  onChange={(e) => {
                    const { checked } = e.target;
                    setF_Course(checked ? [...f_Course, 'MCA'] : f_Course.filter((c) => c !== 'MCA'));
                  }}
                />
                MCA
              </label>
            </div>
            <button type="button" onClick={() => handleUpdate(editingEmployee._id)}>
              Update
            </button>
            <button type="button" onClick={handleCancel}>
              Cancel
            </button>
          </form>
        </div>
      ) : (
        <div>
          {employees.map((employee) => (
            <div key={employee._id} style={{ marginBottom: '10px' }}>
              <button onClick={() => handleEdit(employee)}>Edit</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EmployeeList;
