import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/dashboard.css';

/**
 * Dashboard Component (Citizens)
 * ------------------------------
 * Displays and manages citizen records.
 * Features:
 * - JWT-based secure API calls
 * - Fetch, search, sort, delete citizens
 * - Logout & protected route enforcement
 */

const Dashboard = () => {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('token');

  const [citizens, setCitizens] = useState([]);
  const [filteredCitizens, setFilteredCitizens] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortField, setSortField] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!token) {
      navigate('/');
    }
  }, [navigate, token]);

  // Fetch all citizens on mount
  useEffect(() => {
    fetchCitizens();
  }, []);

  /**
   * Fetch all citizens from backend
   */
  const fetchCitizens = async () => {
    try {
      const response = await axios.get('http://localhost:5000/citizens', {
        headers: { Authorization: `Bearer ${token}` },
        withCredentials: true,
      });
      setCitizens(response.data);
      setFilteredCitizens(response.data);
    } catch (err) {
      console.error('Error fetching citizens:', err);
      setError('Error fetching citizens');
    }
  };

  /**
   * Handle logout
   */
  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/logout', {}, { withCredentials: true });
      localStorage.removeItem('username');
      localStorage.removeItem('token');
      navigate('/');
    } catch (err) {
      console.error('Logout error:', err);
    }
  };

  /**
   * Handle citizen deletion
   */
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/citizens/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSuccess('Citizen deleted successfully');
      setError('');
      fetchCitizens();
    } catch (err) {
      console.error('Error deleting citizen:', err);
      setError('Error deleting citizen');
    }
  };

  /**
   * Handle search (by name or ID)
   */
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    setFilteredCitizens(
      citizens.filter(
        (citizen) =>
          citizen.name.toLowerCase().includes(term) ||
          citizen.userId.toLowerCase().includes(term)
      )
    );
  };

  /**
   * Handle sorting by a given field
   */
  const handleSort = (field) => {
    const order = sortField === field && sortOrder === 'asc' ? 'desc' : 'asc';
    setSortField(field);
    setSortOrder(order);

    const sortedCitizens = [...filteredCitizens].sort((a, b) => {
      if (a[field] < b[field]) return order === 'asc' ? -1 : 1;
      if (a[field] > b[field]) return order === 'asc' ? 1 : -1;
      return 0;
    });

    setFilteredCitizens(sortedCitizens);
  };

  return (
    <div className="dashboard">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">
          <h2>PRIMEAI</h2>
        </div>
        <div className="nav-items">
          <span className="username">Welcome, {username}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      {/* Header Section */}
      <div className="header-section">
        <h1>Citizen Management</h1>
        <button onClick={() => navigate('/register-citizen')}>+ Add Citizen</button>
      </div>

      {/* Search */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by Name or ID"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <span>Total Citizens: {filteredCitizens.length}</span>
      </div>

      {/* Error & Success Messages */}
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      {/* Citizens Table */}
      <table className="citizen-table">
        <thead>
          <tr>
            <th onClick={() => handleSort('userId')}>
              ID <span>{sortField === 'userId' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
            </th>
            <th onClick={() => handleSort('name')}>
              Name <span>{sortField === 'name' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
            </th>
            <th onClick={() => handleSort('email')}>
              Email <span>{sortField === 'email' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
            </th>
            <th>Mobile</th>
            <th onClick={() => handleSort('role')}>
              Role <span>{sortField === 'role' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
            </th>
            <th>Gender</th>
            <th>Skills</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredCitizens.map((citizen) => (
            <tr key={citizen._id}>
              <td>{citizen.userId}</td>
              <td>{citizen.name}</td>
              <td>
                <a href={`mailto:${citizen.email}`}>{citizen.email}</a>
              </td>
              <td>{citizen.mobile}</td>
              <td>{citizen.role}</td>
              <td>{citizen.gender}</td>
              <td>
                {citizen.skills && citizen.skills.length > 0 ? (
                  <ul>
                    {citizen.skills.map((skill, index) => (
                      <li key={index}>{skill}</li>
                    ))}
                  </ul>
                ) : (
                  'No Skills'
                )}
              </td>
              <td>
                <button
                  onClick={() => navigate(`/edit-citizen/${citizen._id}`)}
                  className="edit-btn"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(citizen._id)}
                  className="delete-btn"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
