import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { useNavigate } from 'react-router-dom';
import '../styles/dashboard.css';

const AdminPage = () => {
  const username = localStorage.getItem('username');
  const navigate = useNavigate();
  

  useEffect(() => {
    // Check if user is logged in
    if (!username) {
      // Redirect to login page if not logged in
      navigate('/');
    }
  }, [navigate, username]);
  

  const handleLogout = async () => {
    await axios.post('http://localhost:5000/logout');
    localStorage.removeItem('username');
    navigate('/');
  };
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]); // Filtered employee list for search functionality
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const [sortField, setSortField] = useState('f_Name'); // Default sorting by Name
  const [sortOrder, setSortOrder] = useState('asc'); // Ascending order by default
 

  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/employees');
      setEmployees(response.data);
      setFilteredEmployees(response.data); // Initially set the filtered list to be the full list
    } catch (err) {
      setError('Error fetching employees');
    }
  };

  // Delete an employee
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/delete-employee/${id}`);
      setSuccess('Employee deleted successfully');
      setError('');
      fetchEmployees(); // Refresh the employee list after deletion
    } catch (err) {
      setError('Error deleting employee');
    }
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    setFilteredEmployees(
      employees.filter((employee) =>
        employee.f_Name.toLowerCase().includes(term) ||
        employee.f_Id.toLowerCase().includes(term)
      )
    );
  };

  // Handle sorting
  const handleSort = (field) => {
    const order = sortField === field && sortOrder === 'asc' ? 'desc' : 'asc';
    setSortField(field);
    setSortOrder(order);

    const sortedEmployees = [...filteredEmployees].sort((a, b) => {
      if (a[field] < b[field]) return order === 'asc' ? -1 : 1;
      if (a[field] > b[field]) return order === 'asc' ? 1 : -1;
      return 0;
    });

    setFilteredEmployees(sortedEmployees);
  };

  return (
    <div className="dashboard">
      <nav className="navbar">
        <div className="logo">
          <h2>Dealsdray</h2>
        </div>
        <div className="nav-items">
          <button onClick={() => navigate('/dashboard')}>Home</button>
          <button onClick={() => navigate('/employee')}>Employee List</button>
          <span className="username">Welcome, {username}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </nav>
      <nav className="navbar">
        <div className="logo">
          <h1 style={{ color: "white" }}>Employee List</h1>
        </div>
        <div className="nav-items">
          <button onClick={() => navigate('/register-employee')}>Create Employee</button>
        </div>
      </nav>

      {/* Search Input */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by Name, Email, or ID"
          value={searchTerm}
          onChange={handleSearchChange}
          style={{
            padding: '10px',
            width: '300px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            marginRight: '20px',
          }}
        />
        <span>Total Employees: {filteredEmployees.length}</span> {/* Total count */}
      </div>

      <div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}

        <table className="employee-table">
          <thead>
            <tr>
              <th onClick={() => handleSort('f_Id')}>
                ID <span>{sortField === 'f_Id' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
              </th>
              
              <th onClick={() => handleSort('f_Name')}>
                Name <span>{sortField === 'f_Name' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
              </th>
              <th onClick={() => handleSort('f_Email')}>
                Email <span>{sortField === 'f_Email' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
              </th>
              <th>Mobile</th>
              <th onClick={() => handleSort('f_Designation')}>
                Designation <span>{sortField === 'f_Designation' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
              </th>
              <th>Gender</th>
              <th>Courses</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredEmployees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.f_Id}</td>
                
                <td>{employee.f_Name}</td>
                <td><a href='f_Email'>{employee.f_Email}</a></td>
                <td>{employee.f_Mobile}</td>
                <td>{employee.f_Designation}</td>
                <td>{employee.f_gender}</td>
                <td>
                  {employee.f_Course && employee.f_Course.length > 0 ? (
                    <ul>
                      {employee.f_Course.map((course, index) => (
                        <li key={index}>{course}</li>
                      ))}
                    </ul>
                  ) : (
                    'No Courses'
                  )}
                </td>
                <td>
                  <button
                    onClick={() => navigate(`/edit-employee/${employee._id}`)}
                    style={{ marginRight: '10px' }}
                  >
                    Edit
                  </button>
                  <button onClick={() => handleDelete(employee._id)} style={{ color: 'red' }}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPage;
