import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/updateemployee.css'; // Your specific style file

const EditEmployee = () => {
  const username = localStorage.getItem('username');
  const navigate = useNavigate();

  // --- Theme State (Added for completeness, assuming common practice) ---


  // Function for the logout button
  const handleLogout = async () => {
    await axios.post('http://localhost:5000/logout');
    localStorage.removeItem('username');
    navigate('/');
  };

 

  // Redirect to login if not logged in
  useEffect(() => {
    if (!username) {
      navigate('/');
    }
  }, [navigate, username]);
  
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const [f_Name, setF_Name] = useState('');
  const [f_Id, setF_Id] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Course, setF_Course] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/employees/${id}`);
        const employeeData = response.data;

        setEmployee(employeeData);
        setF_Id(employeeData.f_Id);
        setF_Name(employeeData.f_Name);
        setF_Email(employeeData.f_Email);
        setF_Mobile(employeeData.f_Mobile);
        setF_Designation(employeeData.f_Designation);
        setF_gender(employeeData.f_gender);
        setF_Course(employeeData.f_Course || []);
      } catch (err) {
        setError('Error fetching employee data');
      }
    };

    fetchEmployee();
  }, [id]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const updatedData = {
      f_Id,
      f_Name,
      f_Email,
      f_Mobile,
      f_Designation,
      f_gender,
      f_Course,
    };

    try {
      await axios.put(`http://localhost:5000/update-employee/${id}`, updatedData);
      setSuccess('Employee updated successfully');
      navigate('/dashboard'); // Fixed typo: navigate to '/dashboard'
    } catch (err) {
      console.error('Error updating employee:', err);
      setError('Error updating employee');
    }
  };
  
  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    setF_Course((prevCourses) =>
      prevCourses.includes(value)
        ? prevCourses.filter((course) => course !== value)
        : [...prevCourses, value]
    );
  };

  if (!employee) {
    return <p className="loading-message">Loading...</p>;
  }

 return (
  <div className="employee-form-container">
    <nav className="navbar">
      <div className="logo" onClick={() => navigate('/dashboard')}>
        <h2>PrimeTrade</h2>
      </div>
      <div className="nav-items">
        <span className="username">Welcome, {username}</span>
        <button onClick={handleLogout}>Logout</button>
      </div>
    </nav>

    <h3>Edit Employee: {employee.f_Name}</h3>

    <div className="employee-form-wrapper"> 
      <form onSubmit={handleUpdate}>
        
        {/* Employee ID */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Employee ID"
            value={f_Id}
            onChange={(e) => setF_Id(e.target.value)}
            readOnly
          />
        </div>

        {/* Name, Email, Mobile, Designation (Remaining standard inputs) */}
        <div className="form-group"><input type="text" placeholder="Name" value={f_Name} onChange={(e) => setF_Name(e.target.value)} /></div>
        <div className="form-group"><input type="email" placeholder="Email" value={f_Email} onChange={(e) => setF_Email(e.target.value)} /></div>
        <div className="form-group"><input type="text" placeholder="Mobile" value={f_Mobile} onChange={(e) => setF_Mobile(e.target.value)} /></div>
        <div className="form-group">
          {/* Designation */}
        
          <div><input
            type="text"
            placeholder="Designation"
            value={f_Designation}
            onChange={(e) => setF_Designation(e.target.value)}
          /></div>
         
        </div>

        {/* Gender Selection using Radio Buttons */}
        <div className="form-group radio-group">

          <select
            value={f_gender}
            onChange={(e) => setF_gender(e.target.value)}
          >
            <option value="">Gender</option>
            <option value="Fenale">Female</option>
            <option value="Male">Male</option>
            
          </select>
          
        </div>

        {/* Course Selection - FIXED */}
        <div className="form-group checkbox-group">
          <label>Courses:</label>
          <label><input type="checkbox" value="BSC" checked={f_Course.includes('BSC')} onChange={handleCheckboxChange} />BSC</label>
          <label><input type="checkbox" value="BCA" checked={f_Course.includes('BCA')} onChange={handleCheckboxChange} />BCA</label>
          <label><input type="checkbox" value="MCA" checked={f_Course.includes('MCA')} onChange={handleCheckboxChange} />MCA</label>
        </div>

        {/* Action Buttons (Back/Cancel and Update) */}
        <div className="form-actions">
            <button
              type="button"
              className="back-button"
              onClick={() => navigate('/dashboard')}
            >
              Cancel / Back
            </button>
            <button
              type="submit"
              className="update-button"
            >
              Update Employee
            </button>
        </div>

        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </form>
    </div>
  </div>
);
};

export default EditEmployee;
