import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/createemployee.css';

const EmployeeRegistrationPage = () => {
  const [f_Id, setF_Id] = useState('');
  const [f_Name, setF_Name] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Course, setF_Course] = useState([]);
  const [f_skills, setF_skills] = useState([]);

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate =useNavigate();
  const username = localStorage.getItem('username');

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setF_Course([...f_Course, value]);
    } else {
      setF_Course(f_Course.filter((course) => course !== value));
    }
  };
  useEffect(() => {
    // Check if user is logged in
    if (!username) {
      // Redirect to login page if not logged in
      navigate('/');
    }
  }, [navigate, username]);

  

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!f_Id || !f_Name || !f_Email || !f_Mobile || !f_Designation || !f_skills || !f_gender || !f_Course.length ) {
      setError('Please fill in all fields');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/register-employee', {
        f_Id,
        f_Name,
        f_Email,
        f_Mobile,
        f_Designation,
        f_skills,

        f_gender,
        f_Course,
        // Send base64 string
      }, {
        headers: {
          'Content-Type': 'application/json' // Sending JSON data
        }
      });

      setSuccess(response.data.message);
      setError('');

      // Clear the form fields
      setF_Id('');
      setF_Name('');
      setF_Email('');
      setF_Mobile('');
      setF_Designation('');
      setF_gender('');
      setF_skills([]);
      setF_Course([]);
      
      navigate('/dashboard');
    } catch (err) {
      setSuccess('');
      setError(err.response?.data?.message || 'Something went wrong');
    }
  };
  const handleLogout = async () => {
    await axios.post('http://localhost:5000/logout');
    localStorage.removeItem('username');
    navigate('/');
  };

 /* --- Updated JSX for EmployeeRegistrationPage.js --- */

// ... (imports and functions)

return (
  <div className="employee-form-container"> {/* Use the container class */}
    <nav className="navbar">
      <div className="logo" onClick={() => navigate('/dashboard')}>
        <h2>PrimeTrade</h2>
      </div>
      <div className="nav-items">
        <span className="username">Welcome, {username}</span>
        <button onClick={handleLogout}>Logout</button>
      </div>
    </nav>
    
    <h3>Employee Registration</h3> {/* Use h3 for consistent heading style */}
    
    <div className="employee-form-wrapper">
      <form onSubmit={handleSubmit}>
        
        {/* Employee ID */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Employee ID"
            value={f_Id}
            onChange={(e) => setF_Id(e.target.value)}
          />
        </div>

        {/* Name */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            value={f_Name}
            onChange={(e) => setF_Name(e.target.value)}
          />
        </div>
        
        {/* Email */}
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            value={f_Email}
            onChange={(e) => setF_Email(e.target.value)}
          />
        </div>

        {/* Mobile */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Mobile"
            value={f_Mobile}
            onChange={(e) => setF_Mobile(e.target.value)}
          />
        </div>

        {/* Designation */}
        <div className="form-group">
          <select
            value={f_Designation}
            onChange={(e) => setF_Designation(e.target.value)}
          >
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </div>

        {/* Gender Selection using Radio Buttons */}
        <div className="form-group radio-group">
          <label>Gender:</label>
          <select
            value={f_gender}
            onChange={(e) => setF_gender(e.target.value)}
          >
            <option value="">Gender</option>
            <option value="Fenale">Female</option>
            <option value="Male">Male</option>
            
          </select>
          <label><input type="radio" name="gender" value="Male" checked={f_gender === 'Male'} onChange={(e) => setF_gender(e.target.value)} />Male</label>
          <label><input type="radio" name="gender" value="Female" checked={f_gender === 'Female'} onChange={(e) => setF_gender(e.target.value)} />Female</label>
          <label><input type="radio" name="gender" value="Other" checked={f_gender === 'Other'} onChange={(e) => setF_gender(e.target.value)} />Other</label>
        </div>

        {/* Course Selection using Checkboxes */}
        <div className="form-group checkbox-group">
          <label>Courses:</label>
          <label><input type="checkbox" value="BSC" checked={f_Course.includes('BSC')} onChange={handleCheckboxChange} />BSC</label>
          <label><input type="checkbox" value="BCA" checked={f_Course.includes('BCA')} onChange={handleCheckboxChange} />BCA</label>
          <label><input type="checkbox" value="MCA" checked={f_Course.includes('MCA')} onChange={handleCheckboxChange} />MCA</label>
        </div>
        
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        {/* Action Buttons (Register and Back) */}
        <div className="form-actions">
            <button
              type="button"
              className="back-button"
              onClick={() => navigate('/dashboard')} 
            >
              Cancel / Back
            </button>
            <button 
              type="submit" 
              className="update-button register-button"
            >
              Register Employee
            </button>
        </div>

      </form>
    </div>
  </div>
);
};

export default EmployeeRegistrationPage;
