// src/components/EditEmployee.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EditEmployee = () => {
  const [employee, setEmployee] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();
  const { id } = useParams(); // Get the employee ID from the URL

  useEffect(() => {
    fetchEmployee();
  }, []);

  // Fetch the employee to edit
  const fetchEmployee = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/employees/${id}`);
      setEmployee(response.data);
    } catch (err) {
      setError('Error fetching employee data');
    }
  };

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEmployee({ ...employee, [name]: value });
  };

  // Handle the update
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/update-employee/${id}`, employee);
      setSuccess('Employee updated successfully');
      setError('');
      navigate('/dashboard'); // Navigate back to dashboard after updating
    } catch (err) {
      setError('Error updating employee');
    }
  };

  return (
    <div className="edit-employee">
      <h2>Edit Employee</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      {employee && (
        <form onSubmit={handleUpdate} style={{ display: 'flex', flexDirection: 'column' }}>
          <input
            type="text"
            name="f_Name"
            value={employee.f_Name}
            onChange={handleInputChange}
            placeholder="Name"
            style={{ padding: '8px', marginBottom: '10px' }}
          />
          <input
            type="email"
            name="f_Email"
            value={employee.f_Email}
            onChange={handleInputChange}
            placeholder="Email"
            style={{ padding: '8px', marginBottom: '10px' }}
          />
          <input
            type="text"
            name="f_Mobile"
            value={employee.f_Mobile}
            onChange={handleInputChange}
            placeholder="Mobile"
            style={{ padding: '8px', marginBottom: '10px' }}
          />
          <select
            name="f_Designation"
            value={employee.f_Designation}
            onChange={handleInputChange}
            style={{ padding: '8px', marginBottom: '10px' }}
          >
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
          <button
            type="submit"
            style={{
              padding: '10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
            }}
          >
            Update Employee
          </button>
        </form>
      )}
    </div>
  );
};

export default EditEmployee;
