import React, { useState } from 'react';
import axios from 'axios';

const EmployeeRegistrationPage = () => {
  const [f_Id, setF_Id] = useState('');
  const [f_Name, setF_Name] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Course, setF_Course] = useState('');
  const [f_Image, setF_Image] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleFileChange = (e) => {
    setF_Image(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!f_Id || !f_Name || !f_Email || !f_Mobile || !f_Designation || !f_gender || !f_Course || !f_Image) {
      setError('Please fill in all fields');
      return;
    }

    const formData = new FormData();
    formData.append('f_Id', f_Id);
    formData.append('f_Name', f_Name);
    formData.append('f_Email', f_Email);
    formData.append('f_Mobile', f_Mobile);
    formData.append('f_Designation', f_Designation);
    formData.append('f_gender', f_gender);
    formData.append('f_Course', f_Course);
    formData.append('f_Image', f_Image);

    try {
      const response = await axios.post('http://localhost:5000/register-employee', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setSuccess(response.data.message);
      setError('');

      // Clear the form fields
      setF_Id('');
      setF_Name('');
      setF_Email('');
      setF_Mobile('');
      setF_Designation('');
      setF_gender('');
      setF_Course('');
      setF_Image(null);
    } catch (err) {
      setSuccess('');
      setError(err.response?.data?.message || 'Something went wrong');
    }
  };

  return (
    <div style={{ marginTop: '50px', maxWidth: '600px', margin: 'auto' }}>
      <h2>Employee Registration</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column' }}>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="Employee ID"
            value={f_Id}
            onChange={(e) => setF_Id(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="Name"
            value={f_Name}
            onChange={(e) => setF_Name(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="email"
            placeholder="Email"
            value={f_Email}
            onChange={(e) => setF_Email(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="Mobile"
            value={f_Mobile}
            onChange={(e) => setF_Mobile(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <select
            value={f_Designation}
            onChange={(e) => setF_Designation(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          >
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </div>
        <div style={{ marginBottom: '15px' }}>
          <select
            value={f_gender}
            onChange={(e) => setF_gender(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div style={{ marginBottom: '15px' }}>
          <select
            value={f_Course}
            onChange={(e) => setF_Course(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          >
            <option value="">Select Course</option>
            <option value="BSC">BSC</option>
            <option value="BCA">BCA</option>
            <option value="MCA">MCA</option>
          </select>
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="file"
            accept=".jpg,.jpeg,.png"
            onChange={handleFileChange}
            style={{ width: '100%' }}
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}
        <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}>
          Register
        </button>
      </form>
    </div>
  );
};

export default EmployeeRegistrationPage;
