import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const EditEmployee = () => {
  const { id } = useParams(); // Get employee ID from URL

  const [employee, setEmployee] = useState(null);
  const [f_Id, setF_Id] = useState('');
  const [f_Name, setF_Name] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Course, setF_Course] = useState([]);
  const [, setF_Image] = useState(null); // For file upload
  const [error] = useState('');
  const [success] = useState('');

  useEffect(() => {
    // Fetch employee data based on ID
    const fetchEmployee = async () => {
      try {
        // eslint-disable-next-line no-template-curly-in-string
        const response = await axios.get('http://localhost:5000/employee/${id}');
        const employeeData = response.data;
        setEmployee(employeeData);
        setF_Name(employeeData.f_Name);
        setF_Email(employeeData.f_Email);
        setF_Mobile(employeeData.f_Mobile);
        setF_Designation(employeeData.f_Designation);
        setF_gender(employeeData.f_gender);
        setF_Course(employeeData.f_Course);
      } catch (err) {
        console.error('Error fetching employee data:', err);
      }
    };

    fetchEmployee();
  }, [id]);
  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setF_Course([...f_Course, value]);
    } else {
      setF_Course(f_Course.filter((course) => course !== value));
    }
  };
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setF_Image(reader.result); // Set base64 string
      };
      reader.readAsDataURL(file); // Read file as base64
    }
  };

  const handleUpdate = async () => {
    try {
      const updatedEmployee = {
        f_Name,
        f_Email,
        f_Mobile,
        f_Designation,
        f_gender,
        f_Course,
      };
      await axios.put(`http://localhost:5000/update-employee/${id}`, updatedEmployee);
      alert('Employee updated successfully');
    } catch (err) {
      console.error('Error updating employee:', err);
    }
  };
  if (!employee) {
    return <p>Loading...</p>; // Show a loading message while data is being fetched
  }

  return (
    <div>
      <h3>Edit Employee: {employee.f_Name}</h3>
      <form onSubmit={handleUpdate} style={{ display: 'flex', flexDirection: 'column' }}>
        {/* Employee ID */}
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="Employee ID"
            value={f_Id}
            onChange={(e) => setF_Id(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        
        {/* Name */}
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="Name"
            value={f_Name}
            onChange={(e) => setF_Name(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>

        {/* Email */}
        <div style={{ marginBottom: '15px' }}>
          <input
            type="email"
            placeholder="Email"
            value={f_Email}
            onChange={(e) => setF_Email(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>

        {/* Mobile */}
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="Mobile"
            value={f_Mobile}
            onChange={(e) => setF_Mobile(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          />
        </div>

        {/* Designation */}
        <div style={{ marginBottom: '15px' }}>
          <select
            value={f_Designation}
            onChange={(e) => setF_Designation(e.target.value)}
            style={{ width: '100%', padding: '8px' }}
          >
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </div>

        {/* Gender Selection using Radio Buttons */}
        <div style={{ marginBottom: '15px' }}>
          <label>
            <input
              type="radio"
              name="gender"
              value="Male"
              checked={f_gender === 'Male'}
              onChange={(e) => setF_gender(e.target.value)}
            />
            Male
          </label>
          <label style={{ marginLeft: '15px' }}>
            <input
              type="radio"
              name="gender"
              value="Female"
              checked={f_gender === 'Female'}
              onChange={(e) => setF_gender(e.target.value)}
            />
            Female
          </label>
          <label style={{ marginLeft: '15px' }}>
            <input
              type="radio"
              name="gender"
              value="Other"
              checked={f_gender === 'Other'}
              onChange={(e) => setF_gender(e.target.value)}
            />
            Other
          </label>
        </div>

        {/* Course Selection using Checkboxes */}
        <div style={{ marginBottom: '15px' }}>
          <label>
            <input
              type="checkbox"
              value="BSC"
              checked={f_Course.includes('BSC')}
              onChange={handleCheckboxChange}
            />
            BSC
          </label>
          <label style={{ marginLeft: '15px' }}>
            <input
              type="checkbox"
              value="BCA"
              checked={f_Course.includes('BCA')}
              onChange={handleCheckboxChange}
            />
            BCA
          </label>
          <label style={{ marginLeft: '15px' }}>
            <input
              type="checkbox"
              value="MCA"
              checked={f_Course.includes('MCA')}
              onChange={handleCheckboxChange}
            />
            MCA
          </label>
        </div>

        {/* File Input for Image */}
        <div style={{ marginBottom: '15px' }}>
          <input
            type="file"
            accept=".jpg,.jpeg,.png"
            onChange={handleFileChange}
            style={{ width: '100%' }}
          />
        </div>

        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}

        <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}>
          Update Employee
        </button>
      </form>
    </div>
  );
};

export default EditEmployee;
