import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './dashboard.css';

const AdminPage = () => {
  const [employees, setEmployees] = useState([]);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/employees');
      setEmployees(response.data);
    } catch (err) {
      setError('Error fetching employees');
    }
  };

  // Handle form input changes for editing
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingEmployee({ ...editingEmployee, [name]: value });
  };

  // Update an employee
  const handleUpdate = async (id) => {
    try {
      await axios.put(`http://localhost:5000/update-employee/${id}`, editingEmployee);
      setSuccess('Employee updated successfully');
      setError('');
      fetchEmployees(); // refresh the employee list
      setEditingEmployee(null); // close edit form
    } catch (err) {
      setError('Error updating employee');
    }
  };

  // Delete an employee
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/delete-employee/${id}`);
      setSuccess('Employee deleted successfully');
      setError('');
      fetchEmployees(); // refresh the employee list
    } catch (err) {
      setError('Error deleting employee');
    }
  };

  return (
    <div className="dashboard">
      <nav className="navbar">
        <div className="logo">
          <h1>Employee List</h1>
        </div>
        <div className="nav-items">
          <button onClick={() => navigate('/dashboard')}>Home</button>
          <button onClick={() => navigate('/register-employee')}>New Employee</button>
        </div>
      </nav>

      <div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}

        <table className="employee-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Image</th>
              <th>Name</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Designation</th>
              <th>Gender</th>
              <th>Courses</th> {/* Updated: Display Courses */}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.f_Id}</td>
                <td>
                  {employee.f_Image ? (
                    // eslint-disable-next-line jsx-a11y/alt-text
                    <img
                      src={employee.f_Image}
                      style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                    />
                  ) : (
                    <p>No Image</p>
                  )}
                </td>
                <td>{employee.f_Name}</td>
                <td>{employee.f_Email}</td>
                <td>{employee.f_Mobile}</td>
                <td>{employee.f_Designation}</td>
                <td>{employee.f_gender}</td>
                <td>
                  {employee.f_Course && employee.f_Course.length > 0 ? (
                    <ul>
                      {employee.f_Course.map((course, index) => (
                        <li key={index}>{course}</li>
                      ))}
                    </ul>
                  ) : (
                    'No Courses'
                  )}
                </td> {/* Updated: Show Courses as List */}
                <td>
                  <button
                    onClick={() => setEditingEmployee(employee)}
                    style={{ marginRight: '10px' }}
                  >
                    Edit
                  </button>
                  <button onClick={() => handleDelete(employee._id)} style={{ color: 'red' }}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {editingEmployee && (
          <div>
            <h3>Edit Employee</h3>
            <form
              onSubmit={(e) => {
                e.preventDefault();
                handleUpdate(editingEmployee._id);
              }}
              style={{ display: 'flex', flexDirection: 'column' }}
            >
              <input
                type="text"
                name="f_Name"
                value={editingEmployee.f_Name}
                onChange={handleInputChange}
                placeholder="Name"
                style={{ padding: '8px', marginBottom: '10px' }}
              />
              <input
                type="email"
                name="f_Email"
                value={editingEmployee.f_Email}
                onChange={handleInputChange}
                placeholder="Email"
                style={{ padding: '8px', marginBottom: '10px' }}
              />
              <input
                type="text"
                name="f_Mobile"
                value={editingEmployee.f_Mobile}
                onChange={handleInputChange}
                placeholder="Mobile"
                style={{ padding: '8px', marginBottom: '10px' }}
              />
              <select
                name="f_Designation"
                value={editingEmployee.f_Designation}
                onChange={handleInputChange}
                style={{ padding: '8px', marginBottom: '10px' }}
              >
                <option value="">Select Designation</option>
                <option value="HR">HR</option>
                <option value="Manager">Manager</option>
                <option value="Sales">Sales</option>
              </select>
              <button
                type="submit"
                style={{
                  padding: '10px',
                  backgroundColor: '#4CAF50',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                }}
              >
                Update Employee
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
