import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditEmployee = () => {
  const username = localStorage.getItem('username');
  const navigate = useNavigate();

  const handleLogout = async () => {
    await axios.post('http://localhost:5000/logout');
    localStorage.removeItem('username');
    navigate('/');
  };

  useEffect(() => {
    if (!username) {
      navigate('/');
    }
  }, [navigate, username]);

  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const [f_Name, setF_Name] = useState('');
  const [f_Id, setF_Id] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Image, setF_Image] = useState(null); // For the new image file
  const [existingImageUrl, setExistingImageUrl] = useState(''); // To store the existing image URL
  const [f_Course, setF_Course] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/employees/${id}`);
        const employeeData = response.data;

        setEmployee(employeeData);
        setF_Id(employeeData.f_Id);
        setF_Name(employeeData.f_Name);
        setF_Email(employeeData.f_Email);
        setF_Mobile(employeeData.f_Mobile);
        setF_Designation(employeeData.f_Designation);
        setF_gender(employeeData.f_gender);
        setF_Course(employeeData.f_Course || []);
        setExistingImageUrl(employeeData.f_Image); // Set existing image URL
      } catch (err) {
        setError('Error fetching employee data');
      }
    };

    fetchEmployee();
  }, [id]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    // Append fields to FormData
    formData.append('f_Id', f_Id);
    formData.append('f_Name', f_Name);
    formData.append('f_Email', f_Email);
    formData.append('f_Mobile', f_Mobile);
    formData.append('f_Designation', f_Designation);
    formData.append('f_gender', f_gender);
    formData.append('f_Course', JSON.stringify(f_Course)); // Convert array to JSON string

    // Append the image if there is a new one
    if (f_Image) {
      formData.append('f_Image', f_Image); // Append new image file
    }

    try {
      await axios.put(`http://localhost:5000/update-employee/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data' // Ensure correct content type for file upload
        }
      });

      setSuccess('Employee updated successfully');
      navigate('/employee'); // Redirect to the employee list or desired page
    } catch (err) {
      console.error('Error updating employee:', err);
      setError('Error updating employee');
    }
  };

  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    setF_Course((prevCourses) =>
      prevCourses.includes(value)
        ? prevCourses.filter((course) => course !== value)
        : [...prevCourses, value]
    );
  };

  const handleFileChange = (e) => {
    setF_Image(e.target.files[0]);
  };

  if (!employee) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <nav className="navbar">
        <div className="logo">
          <h2>Dealsdray</h2>
        </div>
        <div className="nav-items">
          <button onClick={() => navigate('/dashboard')}>Home</button>
          <button onClick={() => navigate('/employee')}>Employee List</button>
          <span className="username">Welcome, {username}</span>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </nav>
      <h3>Edit Employee: {employee.f_Name}</h3>
      <div style={{ marginTop: '50px', maxWidth: '600px', margin: 'auto' }}>
        <form onSubmit={handleUpdate} style={{ display: 'flex', flexDirection: 'column' }}>
          <input type="text" placeholder="Employee ID" value={f_Id} onChange={(e) => setF_Id(e.target.value)} style={{ marginBottom: '15px', padding: '8px' }} readOnly />
          <input type="text" placeholder="Name" value={f_Name} onChange={(e) => setF_Name(e.target.value)} style={{ marginBottom: '15px', padding: '8px' }} />
          <input type="email" placeholder="Email" value={f_Email} onChange={(e) => setF_Email(e.target.value)} style={{ marginBottom: '15px', padding: '8px' }} />
          <input type="text" placeholder="Mobile" value={f_Mobile} onChange={(e) => setF_Mobile(e.target.value)} style={{ marginBottom: '15px', padding: '8px' }} />
          <select value={f_Designation} onChange={(e) => setF_Designation(e.target.value)} style={{ marginBottom: '15px', padding: '8px' }}>
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
          <div style={{ marginBottom: '15px' }}>
            <label><input type="radio" name="gender" value="Male" checked={f_gender === 'Male'} onChange={(e) => setF_gender(e.target.value)} /> Male</label>
            <label style={{ marginLeft: '15px' }}><input type="radio" name="gender" value="Female" checked={f_gender === 'Female'} onChange={(e) => setF_gender(e.target.value)} /> Female</label>
            <label style={{ marginLeft: '15px' }}><input type="radio" name="gender" value="Other" checked={f_gender === 'Other'} onChange={(e) => setF_gender(e.target.value)} /> Other</label>
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label><input type="checkbox" value="BSC" checked={f_Course.includes('BSC')} onChange={handleCheckboxChange} /> BSC</label>
            <label style={{ marginLeft: '15px' }}><input type="checkbox" value="BCA" checked={f_Course.includes('BCA')} onChange={handleCheckboxChange} /> BCA</label>
            <label style={{ marginLeft: '15px' }}><input type="checkbox" value="MCA" checked={f_Course.includes('MCA')} onChange={handleCheckboxChange} /> MCA</label>
          </div>
          {existingImageUrl && (
            <div style={{ marginBottom: '15px' }}>
              <p>Existing Image:</p>
              <img src={existingImageUrl} alt="Employee" style={{ width: '150px', height: '150px', objectFit: 'cover' }} />
            </div>
          )}
          <input type="file" onChange={handleFileChange} accept="image/*" style={{ marginBottom: '15px' }} />
          <button type="submit" style={{ padding: '10px', backgroundColor: '#007BFF', color: 'white', border: 'none', borderRadius: '5px' }}>
            Update Employee
          </button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {success && <p style={{ color: 'green' }}>{success}</p>}
        </form>
      </div>
    </div>
  );
};

export default EditEmployee;
