import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './dashboard.css';

const AdminPage = () => {
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]); // Filtered employee list for search functionality
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [sortField, setSortField] = useState('f_Name'); // Default sorting by Name
  const [sortOrder, setSortOrder] = useState('asc'); // Ascending order by default
  const navigate = useNavigate();

  useEffect(() => {
    fetchEmployees();
  }, []);
  

  // Fetch all employees
  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/employees');
      setEmployees(response.data);
      setFilteredEmployees(response.data); // Initially set the filtered list to be the full list
    } catch (err) {
      setError('Error fetching employees');
    }
  };
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditingEmployee({ ...editingEmployee, [name]: value });
  };

  // Update an employee
  const handleUpdate = async (id) => {
    try {
      await axios.put(`http://localhost:5000/update-employee/${id}`, editingEmployee);
      setSuccess('Employee updated successfully');
      setError('');
      fetchEmployees(); // refresh the employee list
      setEditingEmployee(null); // close edit form
    } catch (err) {
      setError('Error updating employee');
    }
  };

  // Delete an employee
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/delete-employee/${id}`);
      setSuccess('Employee deleted successfully');
      setError('');
      fetchEmployees(); // Refresh the employee list after deletion
    } catch (err) {
      setError('Error deleting employee');
    }
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    setFilteredEmployees(
      employees.filter((employee) =>
        employee.f_Name.toLowerCase().includes(term) ||
        employee.f_Id.toLowerCase().includes(term)
      )
    );
  };

  // Handle sorting
  const handleSort = (field) => {
    const order = sortField === field && sortOrder === 'asc' ? 'desc' : 'asc';
    setSortField(field);
    setSortOrder(order);

    const sortedEmployees = [...filteredEmployees].sort((a, b) => {
      if (a[field] < b[field]) return order === 'asc' ? -1 : 1;
      if (a[field] > b[field]) return order === 'asc' ? 1 : -1;
      return 0;
    });

    setFilteredEmployees(sortedEmployees);
  };

  return (
    <div className="dashboard">
      <nav className="navbar">
        <div className="logo">
          <h1>Employee List</h1>
        </div>
        <div className="nav-items">
          <button onClick={() => navigate('/dashboard')}>Home</button>
          <button onClick={() => navigate('/register-employee')}>New Employee</button>
        </div>
      </nav>

      {/* Search Input */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by Name, Email, or ID"
          value={searchTerm}
          onChange={handleSearchChange}
          style={{
            padding: '10px',
            width: '300px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            marginRight: '20px',
          }}
        />
        <span>Total Employees: {filteredEmployees.length}</span> {/* Total count */}
      </div>

      <div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}

        <table className="employee-table">
          <thead>
            <tr>
              
              
              <th onClick={() => handleSort('f_Id')}>
  ID <span>{sortField === 'f_Id' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
</th><th>Image</th>
<th onClick={() => handleSort('f_Name')}>
  Name <span>{sortField === 'f_Name' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
</th>
<th onClick={() => handleSort('f_Email')}>
  Email <span>{sortField === 'f_Email' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
</th> <th>Mobile</th>
<th onClick={() => handleSort('f_Designation')}>
  Designation <span>{sortField === 'f_Designation' ? (sortOrder === 'asc' ? '▲' : '▼') : '▲▼'}</span>
</th>
             
             
              <th>Gender</th>
              <th>Courses</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredEmployees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.f_Id}</td>
                <td>
                  {employee.f_Image ? (
                    <img
                      src={employee.f_Image}
                      alt="Employee"
                      style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                    />
                  ) : (
                    <p>No Image</p>
                  )}
                </td>
                <td>{employee.f_Name}</td>
                <td>{employee.f_Email}</td>
                <td>{employee.f_Mobile}</td>
                <td>{employee.f_Designation}</td>
                <td>{employee.f_gender}</td>
                <td>
                  {employee.f_Course && employee.f_Course.length > 0 ? (
                    <ul>
                      {employee.f_Course.map((course, index) => (
                        <li key={index}>{course}</li>
                      ))}
                    </ul>
                  ) : (
                    'No Courses'
                  )}
                </td>
                <td>
                  <button
                    onClick={() => setEditingEmployee(employee)}
                  style={{ marginRight: '10px' }}
                  >
                    Edit
                  </button>
                  <button onClick={() => handleDelete(employee._id)} style={{ color: 'red' }}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {editingEmployee && (
        <div>
          <h3>Edit Employee</h3>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleUpdate(editingEmployee._id);
            }}
            style={{ display: 'flex', flexDirection: 'column' }}
          >
            
            <input
              type="text"
              name="f_Name"
              value={editingEmployee.f_Name}
              onChange={handleInputChange}
              placeholder="Name"
              style={{ padding: '8px', marginBottom: '10px' }}
            />
            <input
              type="email"
              name="f_Email"
              value={editingEmployee.f_Email}
              onChange={handleInputChange}
              placeholder="Email"
              style={{ padding: '8px', marginBottom: '10px' }}
            />
            <input
              type="text"
              name="f_Mobile"
              value={editingEmployee.f_Mobile}
              onChange={handleInputChange}
              placeholder="Mobile"
              style={{ padding: '8px', marginBottom: '10px' }}
            />
            <select
              type="text"
              name="f_Designation"
              value={editingEmployee.f_Designation}
              onChange={handleInputChange}
              placeholder="Designation"
              style={{ padding: '8px', marginBottom: '10px' }}
            >
                <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
                </select> 
            <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}>
              Update Employee
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
