import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/updateemployee.css';

const EditEmployee = () => {
  const username = localStorage.getItem('username');
  const navigate = useNavigate();

 

  useEffect(() => {
    if (!username) {
      navigate('/');
    }
  }, [navigate, username]);

  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const [f_Name, setF_Name] = useState('');
  const [f_Id, setF_Id] = useState('');
  const [f_Email, setF_Email] = useState('');
  const [f_Mobile, setF_Mobile] = useState('');
  const [f_Designation, setF_Designation] = useState('');
  const [f_gender, setF_gender] = useState('');
  const [f_Course, setF_Course] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/employees/${id}`);
        const employeeData = response.data;

        setEmployee(employeeData);
        setF_Id(employeeData.f_Id);
        setF_Name(employeeData.f_Name);
        setF_Email(employeeData.f_Email);
        setF_Mobile(employeeData.f_Mobile);
        setF_Designation(employeeData.f_Designation);
        setF_gender(employeeData.f_gender);
        setF_Course(employeeData.f_Course || []);
      } catch (err) {
        setError('Error fetching employee data');
      }
    };

    fetchEmployee();
  }, [id]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const updatedData = {
      f_Id,
      f_Name,
      f_Email,
      f_Mobile,
      f_Designation,
      f_gender,
      f_Course,
    };

    try {
      await axios.put(`http://localhost:5000/update-employee/${id}`, updatedData);
      setSuccess('Employee updated successfully');
      navigate('/employee');
    } catch (err) {
      console.error('Error updating employee:', err);
      setError('Error updating employee');
    }
  };

  

  if (!employee) {
    return <p>Loading...</p>;
  }

 return (
  <div className="employee-form-container">
    <nav className="navbar">
      {/* ... navbar content remains the same ... */}
    </nav>

    <h3>Edit Employee: {employee.f_Name}</h3>

    <div className="employee-form-wrapper"> {/* New wrapper class */}
      <form onSubmit={handleUpdate}>
        
        {/* Employee ID */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Employee ID"
            value={f_Id}
            onChange={(e) => setF_Id(e.target.value)}
            readOnly
          />
        </div>

        {/* Name */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            value={f_Name}
            onChange={(e) => setF_Name(e.target.value)}
          />
        </div>
        
        {/* Email */}
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            value={f_Email}
            onChange={(e) => setF_Email(e.target.value)}
          />
        </div>

        {/* Mobile */}
        <div className="form-group">
          <input
            type="text"
            placeholder="Mobile"
            value={f_Mobile}
            onChange={(e) => setF_Mobile(e.target.value)}
          />
        </div>

        {/* Designation */}
        <div className="form-group">
          <select
            value={f_Designation}
            onChange={(e) => setF_Designation(e.target.value)}
          >
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </div>

        {/* Gender */}
        <div className="form-group radio-group">
          <label>Gender:</label>
          {/* ... radio buttons ... */}
        </div>

        {/* Course Selection */}
        <div className="form-group checkbox-group">
          <label>Courses:</label>
          {/* ... checkboxes ... */}
        </div>

        {/* Submit Button */}
        <div className="form-actions"> {/* New wrapper for buttons */}
            <button
              type="button" // Use type="button" to prevent form submission
              className="back-button"
              onClick={() => navigate('/dashboard')} // Navigate back to the employee list
            >
              Cancel / Back
            </button>
            <button
              type="submit"
              className="update-button"
            >
              Update Employee
            </button>
        </div>

        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </form>
    </div>
  </div>
);
};

export default EditEmployee;
